import React, { useEffect } from 'react';
import {
  Box,
  FormControl,
  FormLabel,
  RadioGroup,
  FormControlLabel,
  Radio,
  Checkbox,
  FormGroup,
  Typography
} from '@mui/material';
import RadioButtonUncheckedIcon from '@mui/icons-material/RadioButtonUnchecked';
import RadioButtonCheckedIcon from '@mui/icons-material/RadioButtonChecked';
import { useNavigate } from 'react-router-dom';
import { saveUserAnswers } from '../utils/firebaseUtils';

const Section2Component = ({ name, answers, setAnswers, setValidationError, validationError }) => {
  const navigate = useNavigate();

  // FirestoreÏóê Ï†ÄÏû•
  useEffect(() => {
    if (!name) return;
    saveUserAnswers(name, answers).catch(console.error);
  }, [answers, name]);

  const questions = [
    { id: 'q9',  label: '9. Ïó¨Îü¨ Í∞ÄÏßÄ ÏãùÌíàÍµ∞ÏùÑ Í≥®Í≥†Î£® ÏÑ≠Ï∑®ÌïúÎã§ (Ïòà: Í∑†ÌòïÏãù).' },
    { id: 'q10', label: '10. Ïïî ÏßÑÎã® Î∞è ÏπòÎ£å Ïù¥ÌõÑ, Í∑úÏπôÏ†ÅÏù∏ Ïö¥ÎèôÏùÑ ÌïòÍ≥† ÏûàÎã§.' },
    { id: 'q11', label: '11. Í∑úÏπôÏ†ÅÏù∏ ÏãùÏÇ¨Î•º ÌïúÎã§.' },
    { id: 'q12', label: '12. ÎÇòÎäî ÎÇ¥Í∞Ä ÏÉùÍ∞ÅÌïú Í±¥Í∞ïÍ¥ÄÎ¶¨ Î∞©Î≤ïÏùÑ Ïûò Ïã§Ï≤úÌïòÍ≥† ÏûàÎã§.' },
    { id: 'q13', label: '13. Ïïî ÏßÑÎã® Î∞è ÏπòÎ£å Ïù¥ÌõÑ, ÏãùÏù¥Ï°∞Ï†àÏùÑ ÌïúÎã§.' }
  ];

  const reasons12_1 = [
    '1) Î¨¥ÏóáÏùÑ Ìï¥Ïïº Ìï†ÏßÄ Î™∞ÎùºÏÑú',
    '2) Í±¥Í∞ïÍ¥ÄÎ¶¨ ÏûêÏ≤¥Î•º Ïä§Ìä∏Î†àÏä§ÎùºÍ≥† ÏÉùÍ∞ÅÌï¥ÏÑú',
    '3) ÏùòÏßÄÍ∞Ä ÏóÜÏñ¥ÏÑú',
    '4) ÏãúÍ∞ÑÏù¥ ÎßéÏù¥ Í±∏Î†§ÏÑú',
    '5) Í∞ÄÏ°±Ïù¥ ÎèÑÏôÄÏ£ºÏßÄ ÏïäÏïÑÏÑú',
    '6) Í≤ΩÏ†úÏ†ÅÏúºÎ°ú Î∂ÄÎã¥Ïù¥ ÎêòÏñ¥ÏÑú',
    '7) Í∏∞ÌÉÄ'
  ];

  const sub13 = [
    { id: 'q13_1_1', num: '1)', text: 'Ï°∞ÎØ∏Î£å ÏÑ≠Ï∑®Î•º Ï§ÑÏù∏Îã§.' },
    { id: 'q13_1_2', num: '2)', text: 'ÏãùÌíàÏùò Ïã†ÏÑ†ÎèÑÎ•º Ï§ëÏöîÏãúÌïúÎã§.' },
    { id: 'q13_1_3', num: '3)', text: 'Ï±ÑÏãù Î∞è Í≥ºÏùº ÏúÑÏ£ºÏùò ÏãùÏäµÍ¥ÄÏùÑ ÌïúÎã§.' },
    { id: 'q13_1_4', num: '4)', text: 'Ïú°Î•ò ÏÑ≠Ï∑®Î•º Ï°∞Ï†àÌïúÎã§.' },
    { id: 'q13_1_5', num: '5)', text: 'ÌÉÑÏàòÌôîÎ¨º ÏÑ≠Ï∑®Î•º Ï°∞Ï†àÌïúÎã§.' },
    { id: 'q13_1_6', num: '6)', text: 'Ìï≠ÏïîÏãùÌíà(Ïòà: Î≤ÑÏÑØ, ÎèÑÎùºÏßÄ, ÎëêÏú†, ÌòÑÎØ∏Ïãù Îì±)ÏùÑ Î®πÎäîÎã§.' }
  ];

  const options = [
    { value: '1', label: 'Ï†ÑÌòÄ Í∑∏Î†áÏßÄ ÏïäÎã§' },
    { value: '2', label: 'ÏïΩÍ∞Ñ Í∑∏Î†áÏßÄ ÏïäÎã§' },
    { value: '3', label: 'Î≥¥ÌÜµÏù¥Îã§' },
    { value: '4', label: 'ÏïΩÍ∞Ñ Í∑∏Î†áÎã§' },
    { value: '5', label: 'Îß§Ïö∞ Í∑∏Î†áÎã§' }
  ];

  const handleRadio = (e) => {
    const { name: qId, value } = e.target;

    // q12 Î¨∏Ìï≠ÏóêÏÑú 3, 4, 5Î•º ÏÑ†ÌÉùÌïòÎ©¥ ÏÑπÏÖò5Î°ú Î∞îÎ°ú Ïù¥Îèô (ÌïÑÏàò Ï≤¥ÌÅ¨ Î®ºÏ†Ä!)
    if (qId === 'q12' && ['3', '4', '5'].includes(value)) {
      const updated = { ...answers, [qId]: value };
      // 9,10,11 ÌïÑÏàò Ï≤¥ÌÅ¨ (updatedÎäî Î∞©Í∏à Í∞í Î∞òÏòÅÎê®)
      if (
        !updated.q9 || (typeof updated.q9 === 'string' && updated.q9.trim() === '') ||
        !updated.q10 || (typeof updated.q10 === 'string' && updated.q10.trim() === '') ||
        !updated.q11 || (typeof updated.q11 === 'string' && updated.q11.trim() === '')
      ) {
        setValidationError(true);   // Í≤ΩÍ≥†Ï∞Ω ÎùÑÏö∞Í∏∞
        setAnswers(updated);        // ÎãµÎ≥Ä Î∞òÏòÅÎßå
        return;                     // Ïù¥Îèô ÎßâÍ∏∞
      }
      setAnswers(updated);
      localStorage.setItem('surveyAnswers', JSON.stringify(updated));
      navigate('/section5', { state: { name, fromSkip: true } });
      return;
    }

    // Í∑∏ Ïô∏ Î¨∏Ìï≠ Ï≤òÎ¶¨
    setAnswers((prev) => {
      const updated = { ...prev, [qId]: value };
      if (qId === 'q12' && !['1', '2'].includes(value)) {
        delete updated.q12_reasons;
      }
      return updated;
    });
    setValidationError(false);
  };

  const handleReasons = (e) => {
    const { value } = e.target;
    const prev = answers.q12_reasons || [];
    const next = prev.includes(value)
      ? prev.filter((v) => v !== value)
      : [...prev, value];
    setAnswers((prevAns) => ({ ...prevAns, q12_reasons: next }));
  };

  const validateSection2 = () => {
    const required = ['q9', 'q10', 'q11', 'q12'];
    const missing = required.filter((id) => !answers[id]);
    if (answers.q12 === '1' || answers.q12 === '2') {
      if (!answers.q12_reasons || answers.q12_reasons.length === 0) {
        missing.push('q12_reasons');
      }
    }
    setValidationError(missing.length > 0);
    return missing.length === 0;
  };

  return (
    <Box sx={{ backgroundColor: 'background.paper', p: 3, borderRadius: 2, boxShadow: 1 }}>
      {questions.slice(0, 4).map((q) => (
        <FormControl component="fieldset" key={q.id} sx={{ mb: 2 }} fullWidth>
          <FormLabel component="legend" sx={{ fontWeight: 'bold', color: 'primary.main' }}>
            {q.label}
          </FormLabel>
          {/* 12Î≤à Î¨∏Ìï≠ÏóêÎßå ÏïàÎÇ¥ Î¨∏Íµ¨ Ï∂îÍ∞Ä */}
          {q.id === 'q12' && (
            <>
              <Box sx={{ background: '#f5f7fa', borderRadius: 2, p: 2, mb: 1, borderLeft: '4px solid #1976d2', display: 'flex', alignItems: 'center' }}>
                <Typography variant="body2" sx={{ color: 'text.secondary', fontWeight: 500, fontSize: '1rem' }}>
                  <Box component="span" sx={{ fontSize: '1.2em', mr: 1 }}>‚Äª</Box>
                  <Box component="span" sx={{ color: '#1976d2', fontWeight: 700, fontSize: '1.05em', mr: 1 }}>ÏïàÎÇ¥</Box>
                  <Box component="span" sx={{ color: '#333' }}>
                    <span role="img" aria-label="down">üëá</span> <b>1Î≤à, 2Î≤à</b>ÏùÑ ÏÑ†ÌÉùÌïòÏã† Í≤ΩÏö∞, ÏïÑÎûò Ïù¥Ïú† Ï§ë Ìï¥ÎãπÎêòÎäî Ìï≠Î™©ÏùÑ Î™®Îëê ÏÑ†ÌÉùÌï¥ Ï£ºÏÑ∏Ïöî.<br/>
                    <span role="img" aria-label="fast-forward">‚è©</span> <b>3Î≤à, 4Î≤à, 5Î≤à</b>ÏùÑ ÏÑ†ÌÉùÌïòÏã† Í≤ΩÏö∞, <Box component="span" sx={{ color: '#1976d2', fontWeight: 600, display: 'inline' }}>26Î≤à(ÏÉùÌôúÏäµÍ¥Ä) Î¨∏Ìï≠</Box>ÏúºÎ°ú ÏûêÎèô Ïù¥ÎèôÌï©ÎãàÎã§.
                  </Box>
                </Typography>
              </Box>
              {setValidationError && typeof validationError !== 'undefined' && validationError && (
                <Box sx={{ background: '#fff3e0', borderRadius: 2, p: 1.5, mb: 1, borderLeft: '4px solid #ff9800' }}>
                  <Typography variant="body2" sx={{ color: '#d84315', fontWeight: 600 }}>
                    Ïù¥Ï†Ñ ÏßàÎ¨∏(9, 10, 11Î≤à)ÏùÑ Î™®Îëê ÏôÑÎ£åÌï¥ Ï£ºÏÑ∏Ïöî.
                  </Typography>
                </Box>
              )}
            </>
          )}
          <RadioGroup
            name={q.id}
            value={answers[q.id] || ''}
            onChange={handleRadio}
          >
            {options.map((opt) => (
              <FormControlLabel
                key={opt.value}
                value={opt.value}
                control={<Radio />}
                label={opt.label}
              />
            ))}
          </RadioGroup>
        </FormControl>
      ))}

      {/* Q12-1: q12Í∞Ä 1 ÎòêÎäî 2Ïùº ÎïåÎßå ÌëúÏãú */}
      {['1', '2'].includes(answers.q12) && (
        <Box sx={{ mb: 2 }}>
          <Typography variant="subtitle1" sx={{ fontWeight: 'bold', display: 'block', mb: 1 }}>
            ‚Äª 12-1. Ìï¥ÎãπÎêòÏßÄ ÏïäÎäî Ïù¥Ïú†Î•º Î™®Îëê ÏÑ†ÌÉùÌïòÏÑ∏Ïöî.
          </Typography>
          <FormGroup>
            {reasons12_1.map((reason, idx) => (
              <FormControlLabel
                key={idx}
                control={
                  <Radio
                    checked={answers.q12_reasons?.includes(reason) || false}
                    onChange={handleReasons}
                    name="q12_reasons"
                    value={reason}
                    icon={<RadioButtonUncheckedIcon />}
                    checkedIcon={<RadioButtonCheckedIcon />}
                  />
                }
                label={reason}
              />
            ))}
          </FormGroup>
        </Box>
      )}

      {/* Q13 */}
      <FormControl component="fieldset" sx={{ mb: 2 }} fullWidth>
        <FormLabel component="legend" sx={{ fontWeight: 'bold', color: 'primary.main' }}>
          {questions[4].label}
        </FormLabel>
        <RadioGroup
          name="q13"
          value={answers.q13 || ''}
          onChange={handleRadio}
        >
          {options.map((opt) => (
            <FormControlLabel
              key={opt.value}
              value={opt.value}
              control={<Radio />}
              label={opt.label}
            />
          ))}
        </RadioGroup>
      </FormControl>

      {/* Q13-1: q13Í∞Ä 4 ÎòêÎäî 5Ïùº ÎïåÎßå ÌëúÏãú */}
      {['4', '5'].includes(answers.q13) && (
